export class Imagevalidator {
  constructor() {
    this.acceptType = ['image/jpeg', 'image/png', 'image/tiff', 'image/jpg', 'image/ico'];
  }

  validateType(fileType) {
    return fileType === '' || fileType === undefined ? false : this.acceptType.includes(fileType);
  }

  checkDropped(fileName, files) {
    for (const file of files) {
      if (file.name === fileName) {
        return true;
      }
    }

    return false;
  }

}
